import React from 'react';
const ErrorMessage = ({ children }) => {
  return (
    <div
      style={{
        width: '300px',
        height:'100px',
        padding: 10,
        marginBottom: 10,
        borderRadius: 4,
        backgroundColor: "#ff8993",
        textAlign: "center",
        color: "white",
        textTransform: "capitalize",
        fontSize:'30px'
      }}
    >
      {children}
    </div>
  );
};

export default ErrorMessage;
